CREATE TABLE IF NOT EXISTS jobs ( id uuid PRIMARY KEY DEFAULT gen_random_uuid(), employer_user_id uuid NOT NULL, category varchar(64) NOT NULL, price int NOT NULL DEFAULT 0, description text, geom geography(Point,4326) NOT NULL, status varchar(16) NOT NULL DEFAULT 'OPEN', created_at timestamptz NOT NULL DEFAULT now()); CREATE INDEX IF NOT EXISTS idx_jobs_geom ON jobs USING GIST (geom);